import React, { useEffect, useState } from "react";
import { getRunners, getRunnerByEmail } from "../services/api";
import Header from "../styles/Header.js";
import Footer from "../styles/Footer.js";
import "../styles/Home.css";

const Home = () => {
  const [runners, setRunners] = useState([]);
  const [selectedRunner, setSelectedRunner] = useState(null);
  const [weeks, setWeeks] = useState([]);
  const [error, setError] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    const totalWeeks = 24;
    const currentDate = new Date();
    const marathonDate = new Date("2025-06-22T06:00:00");
    const remainingDays = Math.ceil((marathonDate - currentDate) / (1000 * 60 * 60 * 24));
    const countdown = { weeks: totalWeeks, days: remainingDays };

    // Create 24 weeks structure
    const weekData = Array.from({ length: totalWeeks }, (_, i) => ({
      week: i + 1,
      hasWorkout: false,
      message: `Semana ${i + 1} - Nenhum treino cadastrado`,
    }));

    setWeeks(weekData);
    setWeeks((prevWeeks) =>
      prevWeeks.map((week, index) => ({
        ...week,
        isCurrent: index === totalWeeks - Math.ceil(countdown.days / 7),
      }))
    );
  }, []);

  const handleSearch = () => {
    if (!email) return setError("Por favor, insira um e-mail.");
    setError("");

    getRunnerByEmail(email)
      .then((response) => {
        setSelectedRunner(response.data);
        // Update week structure with runner's workouts
        setWeeks((prevWeeks) =>
          prevWeeks.map((week) => ({
            ...week,
            hasWorkout: response.data.runningWorkouts.some((workout) => workout.week === week.week),
            message: response.data.runningWorkouts.find((workout) => workout.week === week.week)
              ? `Semana ${week.week} - Treino cadastrado`
              : `Semana ${week.week} - Nenhum treino cadastrado`,
          }))
        );
      })
      .catch(() => setError("Usuário não encontrado."));
  };

  const handleWeekClick = (week) => {
    if (!week.hasWorkout) alert(week.message);
  };

  return (
    <div className="container">
      <Header />
      <main className="main-content">
        <h1 className="title">PERSONAL MARATHON</h1>
        {error && <p className="error">{error}</p>}

        <div className="search-container">
          <h2>Buscar Treinos</h2>
          <input
            type="email"
            placeholder="Digite o e-mail do corredor"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input-field"
          />
          <button onClick={handleSearch} className="button">
            Buscar
          </button>
        </div>

        <h2>Semanas Faltantes</h2>
        <div className="weeks-container">
          {weeks.map((week) => (
            <div
              key={week.week}
              className={`week-card ${week.hasWorkout ? "active" : "inactive"}`}
              onClick={() => handleWeekClick(week)}
              style={{
                opacity: week.hasWorkout ? 1 : 0.5,
                cursor: week.hasWorkout ? "pointer" : "not-allowed",
              }}
            >
              <strong>Semana {week.week}</strong>
              {week.isCurrent && <span> - Semana Atual</span>}
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Home;
