import React, { useEffect, useState } from "react";
import { getRunners, getRunnerByEmail, createOrUpdateRunner } from "../services/api";
import Header from "../styles/Header.js";
import Footer from "../styles/Footer.js";
import "../styles/Home.css";

const Home = () => {
  const [runners, setRunners] = useState([]);
  const [selectedRunner, setSelectedRunner] = useState(null);
  const [weeks, setWeeks] = useState([]);
  const [error, setError] = useState("");
  const [email, setEmail] = useState("");
  const [countdown, setCountdown] = useState("");
  const [selectedWeek, setSelectedWeek] = useState(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [newRunner, setNewRunner] = useState({ name: "", email: "" });
  const [expandedWeeks, setExpandedWeeks] = useState([]);
  const [longRunForm, setLongRunForm] = useState({ week: null, distance: "", duration: "" });

  useEffect(() => {
    const currentDate = new Date();
    const marathonDate = new Date("2025-06-22T06:00:00");
    const remainingTime = marathonDate - currentDate;

    if (remainingTime > 0) {
      const days = Math.ceil(remainingTime / (1000 * 60 * 60 * 24));
      const hours = Math.floor((remainingTime / (1000 * 60 * 60)) % 24);
      setCountdown(`${days} dias e ${hours} horas restantes`);
    } else {
      setCountdown("A maratona já passou!");
    }

    // Inicializar estrutura de 24 semanas
    const totalWeeks = 24;
    const weekData = Array.from({ length: totalWeeks }, (_, i) => ({
      week: i + 1,
      hasWorkout: false,
      workout: null,
    }));
    setWeeks(weekData);
  }, []);

  const validateName = (name) => name.trim().length > 0;
  const validateEmail = (email) => {
    const emailPattern = /^[a-zA-Z0-9._-]+@[a-zA-Z0-9.-]+\.[a-zAILESS]{2,6}$/;
    return emailPattern.test(email);
  };

  const handleSearch = () => {
    if (!email) return setError("Por favor, insira um e-mail.");
    setError("");

    getRunnerByEmail(email)
      .then((response) => {
        setSelectedRunner(response.data);

        setWeeks((prevWeeks) =>
          prevWeeks.map((week) => {
            const workout = response.data.runningWorkouts.find(
              (workout) => workout.week === week.week
            );
            return {
              ...week,
              hasWorkout: !!workout,
              workout: workout || null,
            };
          })
        );
      })
      .catch(() => setError("Usuário não encontrado."));
  };

  const toggleWeekExpansion = (weekNumber) => {
    setExpandedWeeks((prev) =>
      prev.includes(weekNumber)
        ? prev.filter((week) => week !== weekNumber)
        : [...prev, weekNumber]
    );
  };

  const handleRegister = (e) => {
    e.preventDefault();
    if (!validateName(newRunner.name)) return setError("Nome inválido.");
    if (!validateEmail(newRunner.email)) return setError("E-mail inválido.");

    createOrUpdateRunner(newRunner)
      .then(() => {
        setRunners([...runners, newRunner]);
        setNewRunner({ name: "", email: "" });
        setShowRegisterForm(false);
        setError("");
      })
      .catch(() => setError("Erro ao cadastrar o usuário."));
  };

  const handleLongRunRegistration = (week) => {
    setLongRunForm({ week: week.week, distance: "", duration: "" });
  };

  const handleSubmitLongRun = () => {
    const { distance, duration, week } = longRunForm;

    if (distance && duration) {
      const url = `http://localhost:8080/workout/${selectedRunner.email}/${week}?distance=${distance}&duration=${duration}`;
      fetch(url, { method: "POST" })
        .then((response) => response.json())
        .then(() => {
          alert("Treino longo registrado com sucesso!");
          setWeeks((prevWeeks) =>
            prevWeeks.map((w) =>
              w.week === week
                ? {
                    ...w,
                    hasWorkout: true,
                    workout: { longRunDistance: distance, longRunDuration: duration },
                  }
                : w
            )
          );
          setLongRunForm({ week: null, distance: "", duration: "" });
        })
        .catch(() => alert("Erro ao registrar o treino longo."));
    } else {
      alert("Preencha todos os campos.");
    }
  };

  return (
    <div className="container">
      <Header />
      <main className="main-content">
        <h1 className="title">PERSONAL MARATHON</h1>
        {error && <p className="error">{error}</p>}

        {/* Mostrar contagem regressiva e busca de treino apenas quando não há corredor selecionado */}
        {!selectedRunner && !showRegisterForm && (
          <div className="countdown-container">
            <h2>Contagem Regressiva para a Maratona</h2>
            <p>{countdown}</p>
          </div>
        )}

        {/* Mostrar o botão de cadastro apenas quando não houver um corredor selecionado */}
        {!selectedRunner && !showRegisterForm && (
          <div className="search-container">
            <h2>Buscar Treinos</h2>
            <input
              type="email"
              placeholder="Digite o e-mail do corredor"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="input-field"
            />
            <button onClick={handleSearch} className="button">
              Buscar
            </button>
          </div>
        )}

        {/* Mostrar o formulário de cadastro apenas quando for necessário */}
        {showRegisterForm && (
          <div className="register-form">
            <h2>Cadastrar Usuário</h2>
            <form onSubmit={handleRegister}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  value={newRunner.name}
                  onChange={(e) => setNewRunner({ ...newRunner, name: e.target.value })}
                  className="input-field"
                />
              </div>
              <div>
                <label>Email:</label>
                <input
                  type="email"
                  value={newRunner.email}
                  onChange={(e) => setNewRunner({ ...newRunner, email: e.target.value })}
                  className="input-field"
                />
              </div>
              <button type="submit" className="button">
                Cadastrar
              </button>
              <button
                type="button"
                onClick={() => setShowRegisterForm(false)}
                className="button-cancel"
              >
                Cancelar
              </button>
            </form>
          </div>
        )}

        {/* Mostrar os detalhes do corredor apenas quando um corredor estiver selecionado */}
        {selectedRunner && (
          <div className="runner-details">
            <h3>Detalhes do Corredor</h3>
            <p>
              <strong>Nome:</strong> {selectedRunner.name || "Não informado"}
            </p>
            <p>
              <strong>Email:</strong> {selectedRunner.email}
            </p>
            <h4>Semanas de Treino:</h4>
            <div className="training-weeks">
              {weeks.map((week) => {
                const workout = selectedRunner?.runningWorkouts.find(
                  (workout) => workout.week === week.week
                );

                return (
                  <div
                    key={week.week}
                    className={`workout-week-card ${
                      expandedWeeks.includes(week.week) ? "expanded" : ""
                    }`}
                  >
                    <div
                      className="week-header"
                      onClick={() => toggleWeekExpansion(week.week)}
                    >
                      <strong>Semana {week.week}</strong>
                    </div>

                    <div className="week-status">
                      {workout ? (
                        <p className="status-registered">Treino cadastrado</p>
                      ) : (
                        <p className="status-not-registered">Nenhum treino cadastrado</p>
                      )}
                    </div>

                    {expandedWeeks.includes(week.week) && (
                      <div className="workout-details">
                        {workout ? (
                          <>
                            {workout.longRunDistance > 0 && (
                              <p>
                                <strong>Treino Longo:</strong> {workout.longRunDistance} km em{" "}
                                {workout.longRunDurationMinutes} minutos (ritmo: {workout.longRunPace})
                              </p>
                            )}
                            <div className="training-week">
                              <p><strong>Segunda:</strong> {workout.trainingWeek.monday}</p>
                              <p><strong>Terça:</strong> {workout.trainingWeek.tuesday}</p>
                              <p><strong>Quarta:</strong> {workout.trainingWeek.wednesday}</p>
                              <p><strong>Quinta:</strong> {workout.trainingWeek.thursday}</p>
                              <p><strong>Sexta:</strong> {workout.trainingWeek.friday}</p>
                              <p><strong>Sábado:</strong> {workout.trainingWeek.saturday}</p>
                              <p><strong>Domingo:</strong> {workout.trainingWeek.sunday}</p>
                            </div>
                          </>
                        ) : (
                          <p>Nenhum treino registrado para essa semana.</p>
                        )}
                        {/* Exibe o formulário para cadastro do treino longo */}
                        {!workout && (
                          <div className="long-run-form">
                            <h4>Cadastrar Treino Longo</h4>
                            <label>Distância (em km):</label>
                            <input
                              type="text"
                              value={longRunForm.distance}
                              onChange={(e) => setLongRunForm({ ...longRunForm, distance: e.target.value })}
                            />
                            <label>Duração (hh:mm:ss):</label>
                            <input
                              type="text"
                              value={longRunForm.duration}
                              onChange={(e) => setLongRunForm({ ...longRunForm, duration: e.target.value })}
                            />
                            <button
                              onClick={handleSubmitLongRun}
                              className="button"
                            >
                              Registrar Treino Longo
                            </button>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
};

export default Home;
