import React, { useEffect, useState } from "react";
import { getRunners } from "../services/api";

const Home = () => {
  const [runners, setRunners] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getRunners()
      .then((response) => setRunners(response.data))
      .catch(() => setError("Erro ao carregar corredores."));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Lista de Corredores</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {runners.map((runner) => (
          <li key={runner.email} style={{ marginBottom: "20px" }}>
            <p><strong>Nome:</strong> {runner.name || "Não informado"}</p>
            <p><strong>Email:</strong> {runner.email}</p>
            <p><strong>Treinos:</strong></p>
            {runner.runningWorkouts.length > 0 ? (
              <ul>
                {runner.runningWorkouts.map((workout) => (
                  <li key={workout.id}>
                    <p>
                      <strong>Semana:</strong> {workout.week}
                    </p>
                    <p>
                      <strong>Treino Longo:</strong> {workout.longRunDistance} km em {workout.longRunDurationMinutes} 
                      (ritmo: {workout.longRunPace})
                    </p>
                    <p>
                      <strong>Detalhes da Semana:</strong>
                    </p>
                    <ul>
                      {Object.entries(workout.trainingWeek).map(([day, activity]) =>
                        day !== "id" ? (
                          <li key={day}>
                            <strong>{day.charAt(0).toUpperCase() + day.slice(1)}:</strong> {activity}
                          </li>
                        ) : null
                      )}
                    </ul>
                  </li>
                ))}
              </ul>
            ) : (
              <p>Nenhum treino registrado.</p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
