import React from "react";
import "../styles/WorkoutWeekCard.css";

const WorkoutWeekCard = ({
  week = {}, // Garante que `week` seja um objeto, mesmo que undefined
  weekIndex,
  weeks,
  setWeeks,
  selectedRunner,
  handleWorkoutRegistration,
  handleUpdateWorkout,
}) => {
  // Mapeamento dos dias da semana em inglês para português
  const daysOfWeek = {
    monday: "Segunda-feira",
    tuesday: "Terça-feira",
    wednesday: "Quarta-feira",
    thursday: "Quinta-feira",
    friday: "Sexta-feira",
    saturday: "Sábado",
    sunday: "Domingo",
  };

  // Garantir que `week.workout` seja um objeto com todos os dias da semana
  const workoutData = {
    monday: "",
    tuesday: "",
    wednesday: "",
    thursday: "",
    friday: "",
    saturday: "",
    sunday: "",
    ...(week.workout || {}), // Mescla com week.workout, se existir
  };

  // Função para lidar com a mudança nos campos dos dias da semana
  const handleChange = (e, day) => {
    const updatedWeeks = [...weeks];
    if (!updatedWeeks[weekIndex].workout) {
      updatedWeeks[weekIndex].workout = {}; // Garante que `workout` seja um objeto
    }
    updatedWeeks[weekIndex].workout[day] = e.target.value; // Atualiza o valor do dia específico
    setWeeks(updatedWeeks); // Atualiza o estado
  };

  // Função para lidar com a mudança nos campos do long run
  const handleLongRunChange = (e, field) => {
    const updatedWeeks = [...weeks];
    updatedWeeks[weekIndex][field] = e.target.value; // Atualiza o campo específico
    setWeeks(updatedWeeks); // Atualiza o estado
  };

  return (
    <div className="workout-week-card-content">
      {/* Renderiza os campos de input para cada dia da semana */}
      {Object.entries(workoutData).map(([day, value]) => {
        if (daysOfWeek[day]) {
          return (
            <div key={day} className="workout-field">
              <label>{daysOfWeek[day]}:</label>
              <input
                type="text"
                value={value || ""} // Garante que o valor não seja undefined
                onChange={(e) => handleChange(e, day)} // Atualiza o valor ao digitar
                className="workout-input"
              />
            </div>
          );
        }
        return null;
      })}

      {/* Campo para a distância do long run */}
      <div className="workout-field">
        <label>Distância do Longão (km):</label>
        <input
          type="number"
          placeholder="Ex: 15"
          value={week.longRunDistance || ""}
          onChange={(e) => handleLongRunChange(e, "longRunDistance")}
          className="workout-input"
        />
      </div>

      {/* Campo para a duração do long run */}
      <div className="workout-field">
        <label>Duração do Longão (hh:mm:ss):</label>
        <input
          type="time"
          step="1"
          value={week.longRunDurationMinutes || ""}
          onChange={(e) => handleLongRunChange(e, "longRunDurationMinutes")}
          className="workout-input"
        />
      </div>

      {/* Campo para o pace do long run (calculado automaticamente) */}
      <div className="workout-field">
        <label>Pace Longão (calculado automaticamente):</label>
        <input
          type="text"
          placeholder="Calculado após inserir km, duração e atualizar"
          value={week.longRunPace || ""}
          readOnly
          className="workout-input"
        />
      </div>

      {/* Botão para registrar ou atualizar o treino */}
      <button
        onClick={() =>
          week.hasWorkout
            ? handleUpdateWorkout(week)
            : handleWorkoutRegistration(week)
        }
        className={`button ${week.hasWorkout ? "workout-update-button" : "workout-register-button"
          }`}
      >
        {week.hasWorkout ? "Atualizar Treino" : "Registrar Treino"}
      </button>
    </div>
  );
};

export default WorkoutWeekCard;
