import React, { useEffect, useState } from "react";
import { getRunners } from "../services/api";
import { Link } from "react-router-dom";

const Runners = () => {
  const [runners, setRunners] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getRunners()
      .then((response) => setRunners(response.data))
      .catch(() => setError("Erro ao carregar corredores."));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Lista de Corredores</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {runners.map((runner) => (
          <li key={runner.email}>
            <p>
              <strong>Nome:</strong> {runner.name || "Não informado"}
            </p>
            <p>
              <strong>Email:</strong> {runner.email}
            </p>
          </li>
        ))}
      </ul>
      <Link to="/">Voltar para a Home</Link>
    </div>
  );
};

export default Runners;
