import React, { useEffect, useState } from "react";
import { getRunners } from "../services/api";
import { Link } from "react-router-dom";
import './Home.css'; // Importe o arquivo de estilos

const Runners = () => {
  const [runners, setRunners] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getRunners()
      .then((response) => setRunners(response.data))
      .catch(() => setError("Erro ao carregar corredores."));
  }, []);

  return (
    <div className="container"> {/* Use a mesma classe container da home */}
      <h1 className="title">Lista de Corredores</h1> {/* Use a classe title */}
      {error && <p className="error">{error}</p>} {/* Use a classe error */}
      <ul>
        {runners.map((runner) => (
          <li key={runner.email}>
            <p>
              <strong>Nome:</strong> {runner.name || "Não informado"}
            </p>
            <p>
              <strong>Email:</strong> {runner.email}
            </p>
          </li>
        ))}
      </ul>
      <Link to="/" className="button">Voltar para a Home</Link> {/* Use a classe button */}
    </div>
  );
};

export default Runners;
